template <std::size_t N>
constexpr std::array<uint8_t, N> &operator+=(std::array<uint8_t, N> &op1, const std::array<uint8_t, N> &op2) {
    for (std::size_t i = 0; i < N; ++i) op1[i] ^= op2[i];
    return op1;
}

template <std::size_t N>
constexpr std::array<uint8_t, N> &operator+=(std::array<uint8_t, N> &op1, const uint8_t (&op2)[N]) {
    for (std::size_t i = 0; i < N; ++i) op1[i] ^= op2[i];
    return op1;
}

