static inline std::array<uint8_t, 16> &linear(std::array<uint8_t, 16> &vector) noexcept {
    for (uint8_t i = 0; i < 16; ++i)
        vector[15 - i] =
            mul_table[0][vector[static_cast<std::size_t>((16 - i)  % 16)]] ^
            mul_table[1][vector[static_cast<std::size_t>((17 - i)  % 16)]] ^
            mul_table[2][vector[static_cast<std::size_t>((18 - i)  % 16)]] ^
            mul_table[3][vector[static_cast<std::size_t>((19 - i)  % 16)]] ^
            mul_table[4][vector[static_cast<std::size_t>((20 - i)  % 16)]] ^
            mul_table[5][vector[static_cast<std::size_t>((21 - i)  % 16)]] ^
                         vector[static_cast<std::size_t>((22 - i)  % 16)]  ^
            mul_table[6][vector[static_cast<std::size_t>((23 - i)  % 16)]] ^
                         vector[static_cast<std::size_t>((24 - i)  % 16)]  ^
            mul_table[5][vector[static_cast<std::size_t>((25 - i)  % 16)]] ^
            mul_table[4][vector[static_cast<std::size_t>((26 - i)  % 16)]] ^
            mul_table[3][vector[static_cast<std::size_t>((27 - i)  % 16)]] ^
            mul_table[2][vector[static_cast<std::size_t>((28 - i)  % 16)]] ^
            mul_table[1][vector[static_cast<std::size_t>((29 - i)  % 16)]] ^
            mul_table[0][vector[static_cast<std::size_t>((30 - i)  % 16)]] ^
                         vector[static_cast<std::size_t>((31 - i)  % 16)];
    return vector;
}

static inline std::array<uint8_t, 16> &inverseLinear(std::array<uint8_t, 16> &vector) noexcept {
    for (uint8_t i = 0; i < 16; ++i) {
        vector[i] =
            mul_table[0][vector[static_cast<std::size_t>((i + 1) % 16)]]  ^
            mul_table[1][vector[static_cast<std::size_t>((i + 2) % 16)]]  ^
            mul_table[2][vector[static_cast<std::size_t>((i + 3) % 16)]]  ^
            mul_table[3][vector[static_cast<std::size_t>((i + 4) % 16)]]  ^
            mul_table[4][vector[static_cast<std::size_t>((i + 5) % 16)]]  ^
            mul_table[5][vector[static_cast<std::size_t>((i + 6) % 16)]]  ^
                         vector[static_cast<std::size_t>((i + 7) % 16)]   ^
            mul_table[6][vector[static_cast<std::size_t>((i + 8) % 16)]]  ^
                         vector[static_cast<std::size_t>((i + 9) % 16)]   ^
            mul_table[5][vector[static_cast<std::size_t>((i + 10) % 16)]] ^
            mul_table[4][vector[static_cast<std::size_t>((i + 11) % 16)]] ^
            mul_table[3][vector[static_cast<std::size_t>((i + 12) % 16)]] ^
            mul_table[2][vector[static_cast<std::size_t>((i + 13) % 16)]] ^
            mul_table[1][vector[static_cast<std::size_t>((i + 14) % 16)]] ^
            mul_table[0][vector[static_cast<std::size_t>((i + 15) % 16)]] ^
                         vector[i];
    }
    return vector;
}

static inline void Feistel(
    std::array<uint8_t, 16> &a1,
    std::array<uint8_t, 16> &a0,
    const uint8_t (&const_round_key)[16]
) noexcept {
    std::array<uint8_t, 16> a1_copy(a1);
    linear(substitute(a1 += const_round_key)) += a0;
    a0 = a1_copy;
}

